import React, { useEffect } from "react";
import api from "../utils/Api";

function Card() {

    const [cards, setCards] = useState([]);

    useEffect(() => {
        api.getAllCards().then((cardList) => {
            setCards(cardList)
        })
    }, [])


return (
<section className="photo-grid">
                {cards.map((cardElement) => (
                        <article className="photo-grid__item" key={cardElement.id}> 
                            <img className="photo-grid__item-image" src={cardElement.link} />
                            <button className="photo-grid__button" aria-label="удаление карточки" type ="button"></button>
                            <div className="photo-grid__item-info">
                            <h2 className="photo-grid__item-info-title">{cardElement.name}</h2>
                            <button className="photo-grid__item-info-like" aria-label="Лайк-сердечко" type ="button"></button>
                            </div>
                            <span className="photo-grid__item-info-counter"></span>
                        </article>
                ))}
            </section>
)
}

export default Card;
