import React, { useState } from 'react';
import headerLogo from '../images/logotip.svg'
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';

function App() {

    const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);

    function handleEditProfileClick() {
        setIsEditProfilePopupOpen(true);
    }

    const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);

    function handleAddPlaceClick() {
        setIsAddPlacePopupOpen(true);
    }

    const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);

    function handleEditAvatarClick() {
        setIsEditAvatarPopupOpen(true);
    }

//    функция handleDeleteCardClick никому не присвоена
    const [isDeleteCardPopupOpen, setIsDeleteCardPopupOpen] = useState(false);

    function handleDeleteCardClick() {
        setIsDeleteCardPopupOpen(true);
    }

    const [selectedCard, setSelectedCard] = useState();

    function handleCardClick(cardElement) {
        setSelectedCard(cardElement);
    }

    function closeAllPopups() {
        setIsEditProfilePopupOpen(false);
        setIsAddPlacePopupOpen(false);
        setIsEditAvatarPopupOpen(false);
        setIsDeleteCardPopupOpen(false);
        setSelectedCard();
    }

    return (
        <div className="page">
            <div className="page__container">
                <Header />
                <Main handleEditProfileClick={handleEditProfileClick} handleAddPlaceClick={handleAddPlaceClick} handleEditAvatarClick={handleEditAvatarClick} onCardClick={handleCardClick} />
                <Footer />
                <PopupWithForm name={'editProfile'} title={"Редактировать профиль"} buttonName={"Сохранить"} children isOpen={isEditProfilePopupOpen} onClose={closeAllPopups}>
                    <div className="form__section">
                        <input className="form__input" type="text" name="name" id="input-name" required minLength="2" maxLength="40" />
                        <span className="form__input-error input-name-error"></span>
                    </div>
                    <div className="form__section">
                        <input className="form__input" type="text" name="occupation" id="input-occupation" required minLength="2" maxLength="200" />
                        <span className="form__input-error input-occupation-error"></span>
                    </div>
                </PopupWithForm>

                <PopupWithForm name={'addCard'} title={'Новое место'} buttonName={'Создать'} children isOpen={isAddPlacePopupOpen} onClose={closeAllPopups}>
                    <div className="form__section">
                        <input className="form__input" type="text" name="titleImage" id="input-titleImage" placeholder="Название" required minLength="2" maxLength="30" />
                        <span className="form__input-error input-titleImage-error"></span>
                    </div>
                    <div className="form__section">
                        <input className="form__input" type="url" name="linkImage" id="input-linkImage" placeholder="Ссылка на картинку" required />
                        <span className="form__input-error input-linkImage-error"></span>
                    </div>
                </PopupWithForm>

                <PopupWithForm name={'updateAvatar'} title={'Обновить аватар'} buttonName={'Сохранить'} children isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups}>
                    <div className="form__section">
                        <input className="form__input" type="url" name="avatarLink" id="input-avatarLink" placeholder="Ссылка на картинку" required />
                        <span className="form__input-error input-avatarLink-error"></span>
                    </div>
                </PopupWithForm>

                <PopupWithForm name={'deleteCard'} title={'Вы уверены?'} buttonName={'Да'} isOpen={isDeleteCardPopupOpen} onClose={closeAllPopups} />
                <ImagePopup cardElement={cardElement} />


                <div className="popup" id="deleteCard">
                    <div className="popup__container">
                        <form className="form" name="deleteCard">
                            <h3 className="form__title form__title_margin">Вы уверены?</h3>
                            <button className="form__submit-button" type="submit">Да</button>
                        </form>
                        <button className="popup__container-close" id="close_popup_deleteCard" aria-label="закрывающий крестик" type="button"></button>
                    </div>
                </div>

                <div className="popup" id="openPic">
                    <div className="popup__container popup__container_background_none">
                        <img className="open-pic" src="#" alt="#" />
                        <p className="open-pic-text"></p>
                        <button className="popup__container-close" id="close_popup-pic" aria-label="закрывающий крестик" type="button"></button>
                    </div>
                </div>

            </div>
        </div>
    );
}

export default App;
